import React, { useEffect, useState } from 'react';
import OrderModal from './OrderModal';

function BuyerProductBrowse() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({ crop_type: '', pin_code: '' });
  const [orderProduct, setOrderProduct] = useState(null);
  const [orderSuccess, setOrderSuccess] = useState('');

  const fetchProducts = async () => {
    setLoading(true);
    setError('');
    try {
      const params = new URLSearchParams();
      if (filters.crop_type) params.append('crop_type', filters.crop_type);
      if (filters.pin_code) params.append('pin_code', filters.pin_code);
      const res = await fetch(`http://localhost:5000/api/products?${params.toString()}`);
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Failed to fetch products');
      } else {
        setProducts(data.products);
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line
  }, []);

  const handleFilterChange = e => {
    setFilters(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleFilterSubmit = e => {
    e.preventDefault();
    fetchProducts();
  };

  const handleOrderClick = product => {
    setOrderProduct(product);
    setOrderSuccess('');
  };

  const handleOrderPlaced = () => {
    setOrderSuccess('Order placed successfully!');
    setOrderProduct(null);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Browse Products</h2>
      <form className="row g-3 mb-4" onSubmit={handleFilterSubmit}>
        <div className="col-md-4">
          <input type="text" className="form-control" name="crop_type" placeholder="Crop Type" value={filters.crop_type} onChange={handleFilterChange} />
        </div>
        <div className="col-md-4">
          <input type="text" className="form-control" name="pin_code" placeholder="Farmer PIN Code (optional)" value={filters.pin_code} onChange={handleFilterChange} />
        </div>
        <div className="col-md-4">
          <button type="submit" className="btn btn-primary w-100">Filter</button>
        </div>
      </form>
      {orderSuccess && <div className="alert alert-success">{orderSuccess}</div>}
      {loading && <div className="text-center my-3"><div className="spinner-border" /></div>}
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {products.map(product => (
          <div className="col-md-4 mb-4" key={product.id}>
            <div className="card h-100 shadow-sm">
              {product.image && (
                <img src={`http://localhost:5000/api/products/images/${product.image}`} alt={product.name} className="card-img-top" style={{ maxHeight: 180, objectFit: 'cover' }} />
              )}
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <div className="mb-2 text-muted">{product.crop_type}</div>
                <div>Price: â‚¹{product.price} / {product.unit}</div>
                <div>Quantity: {product.quantity}</div>
                {product.location && <div>Location: {product.location}</div>}
                <div>Farmer PIN: {product.farmer_pin}</div>
                {product.description && <div className="mt-2"><small>{product.description}</small></div>}
                <button className="btn btn-success mt-3 w-100" onClick={() => handleOrderClick(product)}>Order</button>
              </div>
            </div>
          </div>
        ))}
        {(!loading && products.length === 0) && <div className="text-muted">No products found.</div>}
      </div>
      <OrderModal show={!!orderProduct} onClose={() => setOrderProduct(null)} product={orderProduct} onOrderPlaced={handleOrderPlaced} />
    </div>
  );
}

export default BuyerProductBrowse;
