import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import LandingPage from './LandingPage';
import FarmerDashboard from './FarmerDashboard';
import BuyerDashboard from './BuyerDashboard';
import ProfilePage from './ProfilePage';
import './App.css';

function App() {
  // This state is now primarily for initial routing logic if needed,
  // but dashboards manage their own sessions.
  const [user, setUser] = useState(null);

  // This function can be used to update the App's context of the user,
  // e.g., after login, to potentially guide routing.
  const handleAuthSuccess = (userData) => {
    setUser(userData);
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/register" element={<RegisterPage onRegisterSuccess={handleAuthSuccess} />} />
          <Route path="/login" element={<LoginPage onLoginSuccess={handleAuthSuccess} />} />
          <Route path="/farmer-dashboard" element={<FarmerDashboard />} />
          <Route path="/buyer-dashboard" element={<BuyerDashboard />} />
          <Route path="/profile" element={<ProfilePage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
