import React, { useState } from 'react';
import FarmerProductForm from './FarmerProductForm';
import FarmerProductList from './FarmerProductList';
import FarmerOrderList from './FarmerOrderList';

function FarmerDashboard({ onLogout, user }) {
  const [refresh, setRefresh] = useState(0);
  const [onboarding, setOnboarding] = useState(false);
  const [onboardMsg, setOnboardMsg] = useState('');

  const handleProductAdded = () => {
    setRefresh(r => r + 1);
  };

  const handleStripeOnboard = async () => {
    setOnboarding(true);
    setOnboardMsg('');
    try {
      const res = await fetch('http://localhost:5000/api/payments/onboard-farmer', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ farmer_id: user.id })
      });
      const data = await res.json();
      if (data.success && data.url) {
        window.location.href = data.url;
      } else {
        setOnboardMsg(data.message || 'Failed to start onboarding');
      }
    } catch (err) {
      setOnboardMsg('Network error');
    }
    setOnboarding(false);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome, {user?.name || 'Farmer'}!</h2>
        <button className="btn btn-secondary" onClick={onLogout}>Logout</button>
      </div>
      {/* Stripe onboarding button */}
      {user && !user.stripe_account_id && (
        <div className="mb-4">
          <button className="btn btn-primary" onClick={handleStripeOnboard} disabled={onboarding}>
            {onboarding ? 'Redirecting...' : 'Onboard with Stripe for Payouts'}
          </button>
          {onboardMsg && <div className="text-danger mt-2">{onboardMsg}</div>}
        </div>
      )}
      <div className="row">
        <div className="col-md-7 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerProductList refresh={refresh} />
            </div>
          </div>
        </div>
        <div className="col-md-5 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerProductForm onProductAdded={handleProductAdded} />
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-12 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerOrderList />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FarmerDashboard;
