import React, { useState } from 'react';

function OrderModal({ show, onClose, product, onOrderPlaced }) {
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  if (!show || !product) return null;

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5000/api/orders/place', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ product_id: product.id, quantity })
      });
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Order failed');
      } else {
        setSuccess('Order placed successfully!');
        setQuantity(1);
        if (onOrderPlaced) onOrderPlaced(data.order);
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog" style={{ background: 'rgba(0,0,0,0.4)' }}>
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Order: {product.name}</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="modal-body">
              <div className="mb-2">Crop: <b>{product.crop_type}</b></div>
              <div className="mb-2">Price: â‚¹{product.price} / {product.unit}</div>
              <div className="mb-2">Available: {product.quantity} {product.unit}</div>
              <div className="mb-2">Farmer PIN: {product.farmer_pin}</div>
              <div className="mb-3">
                <label className="form-label">Quantity</label>
                <input type="number" className="form-control" min={1} max={product.quantity} value={quantity} onChange={e => setQuantity(e.target.value)} required />
              </div>
              {error && <div className="alert alert-danger">{error}</div>}
              {success && <div className="alert alert-success">{success}</div>}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={onClose}>Close</button>
              <button type="submit" className="btn btn-success" disabled={loading}>{loading ? 'Placing...' : 'Place Order'}</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default OrderModal;
