import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FarmerProductForm from './FarmerProductForm';
import FarmerProductList from './FarmerProductList';
import FarmerOrderList from './FarmerOrderList';
import MyProductsButton from './MyProductsButton';
import MyOrdersButton from './MyOrdersButton';
import LogoutButton from './LogoutButton';
import { ReactComponent as ProfileIcon } from './assets/profile-icon.svg';

function FarmerDashboard({ onLogout, user }) {
  const [refresh, setRefresh] = useState(0);
  const [view, setView] = useState(null); // null, 'add', 'products', 'orders'

  const handleProductAdded = () => {
    setRefresh(r => r + 1);
  };

  const handleButtonClick = (newView) => {
    if (view === newView) {
      setView(null); // Close if same button clicked
    } else {
      setView(newView); // Open new view
    }
  };

  return (
    <div className="farmer-dashboard-bg">
      <div className="row justify-content-center mx-0">
        <div className="col-md-7 col-lg-6 mb-4">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div className="farmer-dashboard-title" style={{ flexGrow: 1 }}>
              <span style={{fontSize: "25px"}}>Welcome, {user?.name || 'Farmer'}!</span>
              <span className="leaf-icon" role="img" aria-label="leaf">ðŸŒ±</span>
            </div>
            <Link to="/profile" className="profile-icon-link">
              <ProfileIcon />
            </Link>
          </div>
          <div className="farmer-dashboard-card p-4">
            {view === 'add' && <FarmerProductForm onProductAdded={handleProductAdded} />}
            {view === 'products' && <FarmerProductList refresh={refresh} />}
            {view === 'orders' && <FarmerOrderList user={user} />}
            <div className="d-flex flex-column gap-3 mt-4">
              <MyProductsButton onClick={() => handleButtonClick('products')} active={view === 'products'} />
              <MyOrdersButton onClick={() => handleButtonClick('orders')} active={view === 'orders'} />
              <button 
                className={`btn eco-btn w-100${view === 'add' ? ' active-btn' : ''}`}
                style={{ fontWeight: 600 }}
                onClick={() => handleButtonClick('add')}
              >
                Add Product
              </button>
              <LogoutButton onClick={onLogout} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FarmerDashboard;
