import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import logo from './farma-logo.png';
import './OpeningPage.css';

function OpeningPage() {
  const [initialLoad, setInitialLoad] = useState(true);
  const [headerReady, setHeaderReady] = useState(false);

  useEffect(() => {
    // Initial logo centering timeout
    const loadTimer = setTimeout(() => {
      setInitialLoad(false);
      // Additional delay before showing header elements
      setTimeout(() => setHeaderReady(true), 500);
    }, 1500);
    return () => clearTimeout(loadTimer);
  }, []);

  return (
    <div className={`opening-container ${initialLoad ? 'centered' : ''}`}>
      <div className="logo-container">
        <img 
          src={logo} 
          alt="FarmConnect Logo" 
          className={`logo ${initialLoad ? 'pulse' : ''}`} 
        />
      </div>
      <div className={`header-content ${headerReady ? 'visible' : ''}`}>
        <div className="separator"></div>
        <nav className="nav-links">
          <Link to="#about">What is this for?</Link>
          <Link to="#features">Features</Link>
          <Link to="#contact">Contact</Link>
          <Link to="#help">Help</Link>
        </nav>
        <div className="auth-buttons">
          <Link to="/login" className="neon-button">Login</Link>
          <Link to="/register" className="neon-button signup">Sign Up</Link>
        </div>
      </div>
      <div className="bg-effects">
        <div className="light-bar left"></div>
        <div className="light-bar right"></div>
        <div className="glow-circle"></div>
      </div>
    </div>
  );
}

export default OpeningPage;
