import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FarmerProductForm from './FarmerProductForm';
import FarmerProductList from './FarmerProductList';
import FarmerOrderList from './FarmerOrderList';
import MyProductsButton from './MyProductsButton';
import MyOrdersButton from './MyOrdersButton';
import LogoutButton from './LogoutButton';
import { ReactComponent as ProfileIcon } from './assets/profile-icon.svg';

function FarmerDashboard({ onLogout, user }) {
  const [refresh, setRefresh] = useState(0);
  const [view, setView] = useState(null);

  const handleProductAdded = () => {
    setRefresh(r => r + 1);
  };

  const handleButtonClick = (newView) => {
    setView(view === newView ? null : newView);
  };

  return (
    <div className="eco-dashboard-layout">
      {/* Sidebar */}
      <aside className="eco-sidebar">
        <div className="eco-sidebar-header">
          <span className="eco-sidebar-title">Dashboard</span>
        </div>
        <nav className="eco-sidebar-nav">
          <button className={`eco-sidebar-btn${view === 'add' ? ' active' : ''}`} onClick={() => handleButtonClick('add')}>
            Add Product
          </button>
          <button className={`eco-sidebar-btn${view === 'products' ? ' active' : ''}`} onClick={() => handleButtonClick('products')}>
            My Products
          </button>
          <button className={`eco-sidebar-btn${view === 'orders' ? ' active' : ''}`} onClick={() => handleButtonClick('orders')}>
            My Orders
          </button>
          <Link to="/profile" className="eco-sidebar-btn eco-sidebar-link">
            Profile
          </Link>
          <button className="eco-sidebar-btn" onClick={onLogout}>Logout</button>
        </nav>
      </aside>
      {/* Main Content */}
      <main className="eco-dashboard-main">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <div className="farmer-dashboard-title" style={{ flexGrow: 1 }}>
            <span style={{fontSize: "25px"}}>Welcome, {user?.name || 'Farmer'}!</span>
            <span className="leaf-icon" role="img" aria-label="leaf">ðŸŒ±</span>
          </div>
          <Link to="/profile" className="profile-icon-link">
            <ProfileIcon />
          </Link>
        </div>
        <div className="eco-dashboard-content">
          {view === 'add' && <FarmerProductForm onProductAdded={handleProductAdded} />}
          {view === 'products' && <FarmerProductList refresh={refresh} />}
          {view === 'orders' && <FarmerOrderList user={user} />}
          {!view && (
            <div className="text-center text-muted" style={{marginTop: '4rem', fontSize: '1.2rem'}}>
              Select an option from the sidebar to get started.
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default FarmerDashboard;
