import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import BuyerProductBrowse from './BuyerProductBrowse';
import BuyerOrderList from './BuyerOrderList';
import BrowseProductsButton from './BrowseProductsButton';
import BuyerOrdersButton from './BuyerOrdersButton';
import LogoutButton from './LogoutButton';
import { ReactComponent as ProfileIcon } from './assets/profile-icon.svg';

function BuyerDashboard({ onLogout, user }) {
  const [view, setView] = useState(null); // null, 'browse', 'orders'

  const handleButtonClick = (newView) => {
    if (view === newView) {
      setView(null); // Close if same button clicked
    } else {
      setView(newView); // Open new view
    }
  };

  return (
    <div className="farmer-dashboard-bg">
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-md-9 col-lg-8">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <div className="farmer-dashboard-title" style={{ flexGrow: 1 }}>
                <span style={{fontSize: "25px"}}>Welcome, {user?.name || 'Buyer'}!</span>
                <span className="leaf-icon" role="img" aria-label="leaf">ðŸŒ±</span>
              </div>
              <Link to="/profile" className="profile-icon-link">
                <ProfileIcon />
              </Link>
            </div>
            <div className="farmer-dashboard-card p-4">
              {view === 'browse' && <BuyerProductBrowse onReset={() => setView(null)} />}
              {view === 'orders' && <BuyerOrderList user={user} />}
              <div className="d-flex flex-column gap-3 mt-4">
                <BrowseProductsButton onClick={() => handleButtonClick('browse')} active={view === 'browse'} />
                <BuyerOrdersButton onClick={() => handleButtonClick('orders')} active={view === 'orders'} />
                <LogoutButton onClick={onLogout} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BuyerDashboard;
