import React from 'react';
import BuyerProductBrowse from './BuyerProductBrowse';
import BuyerOrderList from './BuyerOrderList';

function BuyerDashboard({ onLogout, user }) {
  return (
    <div className="farmer-dashboard-bg">
      <div className="row justify-content-center mx-0">
        <div className="col-md-7 col-lg-6 mb-4">
          <div className="farmer-dashboard-title">
            <span style={{fontSize: "25px"}}>Welcome, {user?.name || 'Buyer'}!</span>
            <span className="leaf-icon" role="img" aria-label="leaf">ðŸŒ±</span>
          </div>
          <div className="farmer-dashboard-card p-4">
            <BuyerProductBrowse />
            <div className="my-4" />
            <BuyerOrderList />
            <div className="d-flex flex-column gap-3 mt-4">
              <button className="btn btn-outline-danger w-100" style={{ fontWeight: 600 }} onClick={onLogout}>Logout</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BuyerDashboard;
