import React, { useEffect, useState } from 'react';
import { useTranslation } from 'react-i18next';

function FarmerProductList({ refresh }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { t } = useTranslation();

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await fetch('http://localhost:5000/api/products/my', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        if (!data.success) {
          setError(data.message || 'Failed to fetch products');
        } else {
          setProducts(data.products);
        }
      } catch (err) {
        setError('Network error');
      }
      setLoading(false);
    };
    fetchProducts();
  }, [refresh]);

  return (
    <div>
      <h4 className="mb-3">{t('my_products')}</h4>
      {loading && <div className="text-center my-3"><div className="spinner-border" /></div>}
      {error && <div className="alert alert-danger">{error}</div>}
      {products.length === 0 ? (
        <div className="text-muted text-center">{t('no_products')}</div>
      ) : (
        <div className="row">
          {products.map(product => (
            <div className="col-md-6 mb-4" key={product.id}>
              <div className="card shadow-sm">
                {product.image && (
                  <img src={`http://localhost:5000/api/products/images/${product.image}`} alt={product.name} className="card-img-top" style={{ maxHeight: 180, objectFit: 'cover' }} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{product.name}</h5>
                  <p className="card-text mb-1"><b>{t('crop_type')}:</b> {product.crop_type}</p>
                  <p className="card-text mb-1"><b>{t('price_per_unit')}:</b> ₹{product.price}</p>
                  <p className="card-text mb-1"><b>{t('quantity')}:</b> {product.quantity}</p>
                  {product.location && <div>Location: {product.location}</div>}
                  {product.description && <div className="mt-2"><small>{product.description}</small></div>}
                  <button className="btn btn-sm btn-outline-primary me-2" onClick={() => onEdit(product)}>{t('edit')}</button>
                  <button className="btn btn-sm btn-outline-danger" onClick={() => onDelete(product.id)}>{t('delete')}</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default FarmerProductList;
