import React, { useState } from 'react';
import FarmerProductForm from './FarmerProductForm';
import FarmerProductList from './FarmerProductList';
import FarmerOrderList from './FarmerOrderList';

function FarmerDashboard({ onLogout, user }) {
  const [refresh, setRefresh] = useState(0);

  const handleProductAdded = () => {
    setRefresh(r => r + 1);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Welcome, {user?.name || 'Farmer'}!</h2>
        <button className="btn btn-secondary" onClick={onLogout}>Logout</button>
      </div>
      <div className="row">
        <div className="col-md-7 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerProductList refresh={refresh} />
            </div>
          </div>
        </div>
        <div className="col-md-5 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerProductForm onProductAdded={handleProductAdded} />
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-12 mb-4">
          <div className="card shadow-sm">
            <div className="card-body">
              <FarmerOrderList />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FarmerDashboard;
