import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import FarmerDashboard from './FarmerDashboard';
import BuyerDashboard from './BuyerDashboard';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import logo from './farma-logo.png'; // Save your provided logo as farma-logo.png in src

function OpeningPage() {
  const [split, setSplit] = useState(false);
  useEffect(() => {
    const timer = setTimeout(() => setSplit(true), 2000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className={`farma-hero${split ? ' split' : ''}`}> 
      <div className="farma-logo-panel">
        <img src={logo} alt="FarmaConnect Logo" className="farma-logo" />
      </div>
      <div className="farma-separator-vert" />
      <div className="farma-right-panel">
        <nav className="farma-nav">
          <a href="#about">What is this for?</a>
          <a href="#features">Features</a>
          <a href="#contact">Contact</a>
          <a href="#help">Help</a>
        </nav>
        <div className="farma-auth">
          <a href="/login"><button>Login</button></a>
          <a href="/register"><button>Sign Up</button></a>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem('user');
    return u ? JSON.parse(u) : null;
  });

  const handleLogin = (user) => {
    setUser(user);
    window.location.href = user.role === 'farmer' ? '/farmer/dashboard' : '/buyer/dashboard';
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    window.location.href = '/login';
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<OpeningPage />} />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/register" element={<RegisterPage />} />
        <Route path="/farmer/dashboard" element={user && user.role === 'farmer' ? <FarmerDashboard user={user} onLogout={handleLogout} /> : <Navigate to="/login" />} />
        <Route path="/buyer/dashboard" element={user && user.role === 'buyer' ? <BuyerDashboard user={user} onLogout={handleLogout} /> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
