import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

const resources = {
  en: {
    translation: {
      welcome: 'Welcome',
      login: 'Login',
      logout: 'Logout',
      profile: 'Profile',
      language: 'Language',
      save: 'Save',
      // ...add more keys as you go
    },
  },
  hi: {
    translation: {
      welcome: 'स्वागत है',
      login: 'लॉग इन करें',
      logout: 'लॉग आउट',
      profile: 'प्रोफ़ाइल',
      language: 'भाषा',
      save: 'सहेजें',
    },
  },
  te: {
    translation: {
      welcome: 'స్వాగతం',
      login: 'లాగిన్',
      logout: 'లాగౌట్',
      profile: 'ప్రొఫైల్',
      language: 'భాష',
      save: 'సేవ్ చేయి',
    },
  },
  ta: {
    translation: {
      welcome: 'வரவேற்கிறோம்',
      login: 'உள்நுழைய',
      logout: 'வெளியேறு',
      profile: 'சுயவிவரம்',
      language: 'மொழி',
      save: 'சேமிக்க',
    },
  },
  kn: {
    translation: {
      welcome: 'ಸ್ವಾಗತ',
      login: 'ಲಾಗಿನ್',
      logout: 'ಲಾಗೌಟ್',
      profile: 'ಪ್ರೊಫೈಲ್',
      language: 'ಭಾಷೆ',
      save: 'ಉಳಿಸಿ',
    },
  },
};

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    resources,
    fallbackLng: 'en',
    interpolation: {
      escapeValue: false,
    },
    detection: {
      order: ['localStorage', 'navigator'],
      caches: ['localStorage'],
    },
  });

export default i18n;
