import React, { useEffect, useState } from 'react';
import NegotiateModal from './NegotiateModal';
import PaymentModal from './PaymentModal';
import ChatModal from './ChatModal';
import ChatButton from './ChatButton';

function RatingModal({ show, onClose, order, onRated }) {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  if (!show || !order) return null;

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/orders/${order.id}/rate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ rating, comment })
      });
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Rating failed');
      } else {
        setSuccess('Thank you for rating!');
        if (onRated) onRated();
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog" style={{ background: 'rgba(0,0,0,0.4)' }}>
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Rate the Farmer</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="modal-body">
              <div className="mb-3">How would you rate your experience with this farmer?</div>
              <div className="mb-3">
                {[1,2,3,4,5].map(star => (
                  <span key={star} style={{fontSize: 28, color: star <= rating ? '#388e3c' : '#bbb', cursor: 'pointer'}} onClick={() => setRating(star)}>&#9733;</span>
                ))}
              </div>
              <div className="mb-3">
                <textarea className="form-control" placeholder="Optional comment" value={comment} onChange={e => setComment(e.target.value)} rows={2} />
              </div>
              {error && <div className="alert alert-danger">{error}</div>}
              {success && <div className="alert alert-success">{success}</div>}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={onClose}>Close</button>
              <button type="submit" className="btn eco-btn" disabled={loading || !rating}>{loading ? 'Submitting...' : 'Submit Rating'}</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

function BuyerOrderList({ user }) {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionMsg, setActionMsg] = useState('');
  const [negotiateOrder, setNegotiateOrder] = useState(null);
  const [paymentOrder, setPaymentOrder] = useState(null);
  const [chatOrder, setChatOrder] = useState(null);
  const [showRating, setShowRating] = useState(false);
  const [ratingOrder, setRatingOrder] = useState(null);

  const fetchOrders = async () => {
    setLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5000/api/orders/my', {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Failed to fetch orders');
      } else {
        setOrders(data.orders);
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleAccept = async (orderId) => {
    setActionMsg('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/orders/buyer-accept/${orderId}`, {
        method: 'PATCH',
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (!data.success) {
        setActionMsg(data.message || 'Failed to accept negotiation');
      } else {
        setActionMsg(data.message);
        fetchOrders();
      }
    } catch (err) {
      setActionMsg('Network error');
    }
  };

  const handleNegotiate = (order) => {
    setNegotiateOrder(order);
    setActionMsg('');
  };

  const handleNegotiated = () => {
    setNegotiateOrder(null);
    setActionMsg('Counter-offer sent!');
    fetchOrders();
  };

  const handleCancel = async (orderId) => {
    setActionMsg('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/orders/buyer-cancel/${orderId}`, {
        method: 'PATCH',
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (!data.success) {
        setActionMsg(data.message || 'Failed to cancel order');
      } else {
        setActionMsg('Order cancelled.');
        fetchOrders();
      }
    } catch (err) {
      setActionMsg('Network error');
    }
  };

  const handlePayment = (order) => {
    setPaymentOrder(order);
    setActionMsg('');
  };

  const handlePaymentSuccess = async (order) => {
    setPaymentOrder(null);
    setActionMsg('Payment successful! Order has been paid.');
    // Check if already rated
    const token = localStorage.getItem('token');
    const res = await fetch(`http://localhost:5000/api/orders/${order.id}/rate`, {
      method: 'GET',
      headers: { Authorization: `Bearer ${token}` }
    });
    const data = await res.json();
    if (!data.success || !data.rated) {
      setRatingOrder(order);
      setShowRating(true);
    }
    fetchOrders();
  };

  const handleMarkAsRead = (orderId) => {
    setOrders(prevOrders =>
      prevOrders.map(o => 
        o.id === orderId ? { ...o, unread_messages: 0 } : o
      )
    );
  };

  return (
    <div>
      <h5 className="mb-3">My Orders</h5>
      {actionMsg && <div className="alert alert-info">{actionMsg}</div>}
      {loading && <div className="text-center my-3"><div className="spinner-border" /></div>}
      {error && <div className="alert alert-danger">{error}</div>}
      <div className="row">
        {orders.map(order => (
          <div className="col-md-6 mb-3" key={order.id}>
            <div className="card h-100 shadow-sm">
              {order.product_image && (
                <img src={`http://localhost:5000/api/products/images/${order.product_image}`} alt={order.product_name} className="card-img-top" style={{ maxHeight: 180, objectFit: 'cover' }} />
              )}
              <div className="card-body">
                <h5 className="card-title">{order.product_name}</h5>
                <div>Quantity: {order.quantity}</div>
                <div>Price: ₹{order.price}</div>
                {order.negotiated_price && <div>Negotiated Price: <b>₹{order.negotiated_price}</b></div>}
                <div>Status: <b>{order.status}</b></div>
                <div className="text-muted mt-2" style={{ fontSize: '0.9em' }}>Ordered on: {new Date(order.created_at).toLocaleString()}</div>
                <div className="mt-3 d-flex gap-2 flex-wrap">
                  {(['negotiation', 'farmer_accepted'].includes(order.status)) && <button className="btn btn-success btn-sm" onClick={() => handleAccept(order.id)}>Accept</button>}
                  {order.status === 'negotiation' && <button className="btn btn-warning btn-sm" onClick={() => handleNegotiate(order)}>Counter Offer</button>}
                  {['negotiation', 'pending', 'buyer_accepted', 'farmer_accepted'].includes(order.status) && <button className="btn btn-outline-danger btn-sm" onClick={() => handleCancel(order.id)}>Cancel</button>}
                  {order.status === 'accepted' && <button className="btn eco-btn btn-sm" onClick={() => handlePayment(order)}>Proceed to Payment</button>}
                  {order.status === 'paid' && (
                    <div className="paid-indicator">
                      <span className="icon">✓</span> Paid
                    </div>
                  )}
                  <ChatButton 
                    onClick={() => setChatOrder(order)} 
                    unreadCount={order.unread_messages} 
                  />
                </div>
              </div>
            </div>
          </div>
        ))}
        {(!loading && orders.length === 0) && <div className="text-muted">No orders found.</div>}
      </div>
      <NegotiateModal show={!!negotiateOrder} onClose={() => setNegotiateOrder(null)} order={negotiateOrder} onNegotiated={handleNegotiated} role="buyer" />
      <PaymentModal show={!!paymentOrder} onClose={() => setPaymentOrder(null)} order={paymentOrder} onPaymentSuccess={handlePaymentSuccess} />
      <RatingModal show={showRating} onClose={() => setShowRating(false)} order={ratingOrder} onRated={() => { setShowRating(false); fetchOrders(); }} />
      <ChatModal 
        show={!!chatOrder} 
        onClose={() => setChatOrder(null)} 
        order={chatOrder} 
        currentUser={user}
        onMarkAsRead={handleMarkAsRead}
      />
    </div>
  );
}

export default BuyerOrderList;
