import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import FarmerProductForm from './FarmerProductForm';
import FarmerProductList from './FarmerProductList';
import FarmerOrderList from './FarmerOrderList';
import Sidebar from './Sidebar';
import DashboardWelcome from './DashboardWelcome';
import './DashboardLayout.css';

// Import Icons
import { ReactComponent as AddCircleIcon } from './assets/add-circle-icon.svg';
import { ReactComponent as ListIcon } from './assets/list-icon.svg';

const FarmerDashboard = () => {
  const [view, setView] = useState(null);
  const [user, setUser] = useState({ name: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }
        // Fetch user's name to display on the dashboard
        const res = await fetch('/api/profile', {
          headers: { 'x-auth-token': token }
        });
        if (res.ok) {
          const data = await res.json();
          setUser({ name: data.name });
        } else {
          throw new Error('Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        localStorage.removeItem('token');
        navigate('/login');
      }
    };
    fetchUserData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const renderView = () => {
    switch (view) {
      case 'add':
        return <FarmerProductForm />;
      case 'list':
        return <FarmerProductList />;
      case 'orders':
        return <FarmerOrderList />;
      default:
        return <DashboardWelcome name={user.name} />;
    }
  };

  const handleNavItemClick = (viewName) => {
    setView(currentView => (currentView === viewName ? null : viewName));
  }

  const navItems = [
    { key: 'add', label: 'Add Product', icon: <AddCircleIcon /> },
    { key: 'list', label: 'My Products', icon: <ListIcon /> },
    { key: 'orders', label: 'My Orders', icon: <ListIcon /> },
  ];

  return (
    <div className="dashboard-layout">
      <Sidebar 
        user={user}
        navItems={navItems}
        activeView={view}
        onNavItemClick={handleNavItemClick}
        onLogout={handleLogout}
      />
      <main className="main-content-area">
        {renderView()}
      </main>
    </div>
  );
};

export default FarmerDashboard;
