import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

function FarmerProductForm({ onProductAdded }) {
  const [form, setForm] = useState({
    name: '',
    description: '',
    crop_type: '',
    price: '',
    quantity: '',
    unit: '',
    location: '',
    image: null
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { t } = useTranslation();

  const handleChange = e => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setForm(f => ({ ...f, image: files[0] }));
    } else {
      setForm(f => ({ ...f, [name]: value }));
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const token = localStorage.getItem('token');
      const data = new FormData();
      Object.entries(form).forEach(([key, value]) => {
        if (value) data.append(key, value);
      });
      const res = await fetch('http://localhost:5000/api/products/add', {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
        body: data
      });
      const result = await res.json();
      if (!result.success) {
        setError(result.message || 'Failed to add product');
      } else {
        setSuccess('Product added successfully!');
        setForm({ name: '', description: '', crop_type: '', price: '', quantity: '', unit: '', location: '', image: null });
        if (onProductAdded) onProductAdded(result.product);
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4 border p-3 rounded bg-light shadow-sm">
      <h5 className="mb-3">{t('add_product')}</h5>
      <div className="row">
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('product_name')}</label>
          <input type="text" className="form-control" name="name" placeholder={t('product_name')} value={form.name} onChange={handleChange} required />
        </div>
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('crop_type')}</label>
          <input type="text" className="form-control" name="crop_type" placeholder={t('crop_type')} value={form.crop_type} onChange={handleChange} required />
        </div>
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('price_per_unit')}</label>
          <input type="number" className="form-control" name="price" placeholder={t('price_per_unit')} value={form.price} onChange={handleChange} required />
        </div>
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('quantity')}</label>
          <input type="number" className="form-control" name="quantity" placeholder={t('quantity')} value={form.quantity} onChange={handleChange} required />
        </div>
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('unit')}</label>
          <input type="text" className="form-control" name="unit" placeholder={t('unit')} value={form.unit} onChange={handleChange} required />
        </div>
        <div className="col-md-6 mb-3">
          <label className="form-label">{t('location')}</label>
          <input type="text" className="form-control" name="location" placeholder={t('location')} value={form.location} onChange={handleChange} />
        </div>
        <div className="col-12 mb-3">
          <label className="form-label">{t('description')}</label>
          <textarea className="form-control" name="description" placeholder={t('description')} value={form.description} onChange={handleChange} rows={2} />
        </div>
        <div className="col-12 mb-3">
          <label className="form-label">{t('upload_image')}</label>
          <input type="file" className="form-control" name="image" accept="image/*" onChange={handleChange} />
        </div>
      </div>
      {error && <div className="alert alert-danger">{t(error) || error}</div>}
      {success && <div className="alert alert-success">{success}</div>}
      <button type="submit" className="btn btn-success" disabled={loading}>{loading ? t('adding') : t('add_product_btn')}</button>
    </form>
  );
}

export default FarmerProductForm;
