import React, { useEffect, useState } from 'react';

function FarmerProductList({ refresh }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [deleteSuccess, setDeleteSuccess] = useState('');
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [productToDelete, setProductToDelete] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError('');
      try {
        const token = localStorage.getItem('token');
        const res = await fetch('http://localhost:5000/api/products/my', {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data = await res.json();
        if (!data.success) {
          setError(data.message || 'Failed to fetch products');
        } else {
          setProducts(data.products);
        }
      } catch (err) {
        setError('Network error');
      }
      setLoading(false);
    };
    fetchProducts();
  }, [refresh]);

  const handleDeleteClick = (product) => {
    setProductToDelete(product);
    setShowDeleteModal(true);
  };

  const handleDeleteConfirm = async () => {
    if (!productToDelete) return;
    
    setDeleteLoading(true);
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/products/${productToDelete.id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Failed to delete product');
      } else {
        setDeleteSuccess('Product deleted successfully!');
        setProducts(products.filter(p => p.id !== productToDelete.id));
        setShowDeleteModal(false);
        setProductToDelete(null);
        // Clear success message after 3 seconds
        setTimeout(() => setDeleteSuccess(''), 3000);
      }
    } catch (err) {
      setError('Network error');
    }
    setDeleteLoading(false);
  };

  const handleDeleteCancel = () => {
    setShowDeleteModal(false);
    setProductToDelete(null);
  };

  return (
    <div>
      <h5 className="mb-3">My Products</h5>
      {deleteSuccess && <div className="alert alert-success">{deleteSuccess}</div>}
      {error && <div className="alert alert-danger">{error}</div>}
      {loading && <div className="text-center my-3"><div className="spinner-border" /></div>}
      
      <div className="row">
        {products.map(product => (
          <div className="col-md-6 mb-3" key={product.id}>
            <div className="card h-100 shadow-sm">
              {product.image && (
                <img src={`http://localhost:5000/api/products/images/${product.image}`} alt={product.name} className="card-img-top" style={{ maxHeight: 180, objectFit: 'cover' }} />
              )}
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{product.name}</h5>
                <div className="mb-2 text-muted">{product.crop_type}</div>
                <div>Price: â‚¹{product.price} / {product.unit}</div>
                <div>Quantity: {product.quantity}</div>
                {product.location && <div>Location: {product.location}</div>}
                {product.description && <div className="mt-2"><small>{product.description}</small></div>}
                <div className="mt-auto pt-3">
                  <button 
                    className="btn btn-outline-danger btn-sm w-100" 
                    onClick={() => handleDeleteClick(product)}
                  >
                    Delete Product
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
        {(!loading && products.length === 0) && <div className="text-muted">No products found.</div>}
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <>
          <div className="modal fade show" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }} tabIndex="-1">
            <div className="modal-dialog modal-dialog-centered">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Confirm Delete</h5>
                  <button type="button" className="btn-close" onClick={handleDeleteCancel}></button>
                </div>
                <div className="modal-body">
                  <p>Are you sure you want to delete <strong>"{productToDelete?.name}"</strong>?</p>
                  <p className="text-muted">This action cannot be undone.</p>
                </div>
                <div className="modal-footer">
                  <button type="button" className="btn btn-secondary" onClick={handleDeleteCancel}>
                    Cancel
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-danger" 
                    onClick={handleDeleteConfirm}
                    disabled={deleteLoading}
                  >
                    {deleteLoading ? 'Deleting...' : 'Delete'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default FarmerProductList;
