import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import BuyerProductBrowse from './BuyerProductBrowse';
import BuyerOrderList from './BuyerOrderList';
import BrowseProductsButton from './BrowseProductsButton';
import BuyerOrdersButton from './BuyerOrdersButton';
import LogoutButton from './LogoutButton';
import { ReactComponent as ProfileIcon } from './assets/profile-icon.svg';

function BuyerDashboard({ onLogout, user }) {
  const [view, setView] = useState(null);

  const handleButtonClick = (newView) => {
    setView(view === newView ? null : newView);
  };

  return (
    <div className="eco-dashboard-layout">
      {/* Sidebar */}
      <aside className="eco-sidebar">
        <div className="eco-sidebar-profile">
          <Link to="/profile" className="profile-icon-link">
            <ProfileIcon />
          </Link>
          <span className="eco-sidebar-username">{user?.name || 'Buyer'}</span>
        </div>
        <nav className="eco-sidebar-nav">
          <button className={`eco-sidebar-btn${view === 'browse' ? ' active' : ''}`} onClick={() => handleButtonClick('browse')}>
            Browse Products
          </button>
          <button className={`eco-sidebar-btn${view === 'orders' ? ' active' : ''}`} onClick={() => handleButtonClick('orders')}>
            My Orders
          </button>
          <Link to="/profile" className="eco-sidebar-btn eco-sidebar-link">
            Profile
          </Link>
          <button className="eco-sidebar-btn" onClick={onLogout}>Logout</button>
        </nav>
      </aside>
      {/* Main Content */}
      <main className="eco-dashboard-main">
        <div style={{height: 32, marginBottom: 24}} />
        <div className="eco-dashboard-content">
          {view === 'browse' && <BuyerProductBrowse onReset={() => setView(null)} />}
          {view === 'orders' && <BuyerOrderList user={user} />}
          {!view && (
            <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '340px'}}>
              <img src={require('./greenlogo.png')} alt="FarmConnect Logo" style={{width: '160px', height: '160px', objectFit: 'contain', marginBottom: '1.2rem', filter: 'drop-shadow(0 4px 16px rgba(34,139,34,0.13))'}} />
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default BuyerDashboard;
