import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import LoginPage from './LoginPage';
import RegisterPage from './RegisterPage';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [user, setUser] = useState(() => {
    const u = localStorage.getItem('user');
    return u ? JSON.parse(u) : null;
  });

  const handleLogin = (user) => {
    setUser(user);
    window.location.href = user.role === 'farmer' ? '/farmer/dashboard' : '/buyer/dashboard';
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    window.location.href = '/login';
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route path="/register" element={<RegisterPage />} />
        {/* Placeholder dashboards */}
        <Route path="/farmer/dashboard" element={user && user.role === 'farmer' ? <div className="container mt-5"><h2>Farmer Dashboard</h2><button className="btn btn-secondary" onClick={handleLogout}>Logout</button></div> : <Navigate to="/login" />} />
        <Route path="/buyer/dashboard" element={user && user.role === 'buyer' ? <div className="container mt-5"><h2>Buyer Dashboard</h2><button className="btn btn-secondary" onClick={handleLogout}>Logout</button></div> : <Navigate to="/login" />} />
        {/* Default route */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
