import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import './Sidebar.css';
import { ReactComponent as ProfileIcon } from './assets/profile-icon.svg';
import { ReactComponent as LogoutIcon } from './assets/logout-icon.svg'; // Will create this icon

function Sidebar({ user, navItems, activeView, onNavItemClick, onLogout }) {
  return (
    <aside className="sidebar">
      <div className="sidebar-header">
        <h3>FarmConnect</h3>
      </div>
      <div className="sidebar-profile">
        <ProfileIcon className="sidebar-profile-icon" />
        <div className="sidebar-profile-info">
          <span>Welcome,</span>
          <h5>{user.name}</h5>
        </div>
      </div>
      <nav className="sidebar-nav">
        <ul>
          {navItems.map(item => (
            <li key={item.key}>
              <button 
                className={`sidebar-link ${activeView === item.key ? 'active' : ''}`}
                onClick={() => onNavItemClick(item.key)}
              >
                {item.icon}
                <span>{item.label}</span>
              </button>
            </li>
          ))}
          <li>
            <NavLink to="/profile" className="sidebar-link">
              <ProfileIcon />
              <span>My Profile</span>
            </NavLink>
          </li>
        </ul>
      </nav>
      <div className="sidebar-footer">
        <button onClick={onLogout} className="sidebar-link logout-btn">
          <LogoutIcon />
          <span>Logout</span>
        </button>
      </div>
    </aside>
  );
}

export default Sidebar;
