import React, { useEffect, useState } from 'react';
import './LandingPage.css';
import logo from './assets/farmaconnect_logo.png';

const navLinks = [
  { label: 'About', href: '#' },
  { label: 'How it Works', href: '#' },
  { label: 'Contact', href: '#' },
  { label: 'FAQ', href: '#' },
];

function LandingPage({ onLogin, onRegister }) {
  const [logoAtTop, setLogoAtTop] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setLogoAtTop(true), 1200);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="landing-bg">
      <div className={`logo-nav-row ${logoAtTop ? 'show' : ''}`}>
        <div className={`logo-animator ${logoAtTop ? 'to-top-left' : ''}`}>
          <img src={logo} alt="FarmaConnect Logo" className="main-logo" />
        </div>
        <nav className="nav-links">
          {navLinks.map(link => (
            <a key={link.label} href={link.href}>{link.label}</a>
          ))}
        </nav>
      </div>
      {!logoAtTop && (
        <div className="logo-animator">
          <img src={logo} alt="FarmaConnect Logo" className="main-logo" />
        </div>
      )}
      {logoAtTop && (
        <div className="center-buttons fade-in">
          <button className="btn btn-success btn-lg mx-2" onClick={onLogin}>Login</button>
          <button className="btn btn-outline-success btn-lg mx-2" onClick={onRegister}>Sign Up</button>
        </div>
      )}
    </div>
  );
}

export default LandingPage;
