import React, { useState, useEffect } from 'react';

function OrderModal({ show, onClose, product, onOrderPlaced }) {
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Reset form when modal opens/closes
  useEffect(() => {
    if (show && product) {
      setQuantity(1);
      setError('');
      setSuccess('');
    }
  }, [show, product]);

  if (!show || !product) return null;

  const handleSubmit = async e => {
    e.preventDefault();
    
    if (quantity <= 0) {
      setError('Quantity must be greater than 0');
      return;
    }
    
    if (quantity > product.quantity) {
      setError(`Only ${product.quantity} ${product.unit} available`);
      return;
    }
    
    setLoading(true);
    setError('');
    setSuccess('');
    
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('http://localhost:5000/api/orders/place', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ product_id: product.id, quantity })
      });
      
      const data = await res.json();
      
      if (!data.success) {
        setError(data.message || 'Order failed');
        // If product not found, close modal and refresh
        if (res.status === 404) {
          setTimeout(() => {
            onClose();
            if (onOrderPlaced) onOrderPlaced();
          }, 2000);
        }
      } else {
        setSuccess(data.message || 'Order placed successfully!');
        setQuantity(1);
        setTimeout(() => {
          if (onOrderPlaced) onOrderPlaced(data.order);
        }, 1500);
      }
    } catch (err) {
      setError('Network error. Please check your connection and try again.');
    }
    setLoading(false);
  };

  return (
    <div className="modal show d-block" tabIndex="-1" role="dialog" style={{ background: 'rgba(0,0,0,0.4)' }}>
      <div className="modal-dialog modal-dialog-centered" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Order: {product.name}</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="modal-body">
              <div className="mb-2">Crop: <b>{product.crop_type}</b></div>
              <div className="mb-2">Price: ₹{product.price} / {product.unit}</div>
              <div className="mb-2">Available: {product.quantity} {product.unit}</div>
              <div className="mb-2">Farmer PIN: {product.farmer_pin}</div>
              <div className="mb-3">
                <label className="form-label">Quantity</label>
                <input 
                  type="number" 
                  className="form-control" 
                  min={1} 
                  max={product.quantity} 
                  value={quantity} 
                  onChange={e => setQuantity(parseInt(e.target.value) || 1)} 
                  required 
                />
                <div className="form-text">Total: ₹{product.price * quantity}</div>
              </div>
              {error && <div className="alert alert-danger">{error}</div>}
              {success && <div className="alert alert-success">{success}</div>}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={onClose}>Close</button>
              <button 
                type="submit" 
                className="btn btn-success" 
                disabled={loading || quantity <= 0 || quantity > product.quantity}
              >
                {loading ? 'Placing...' : 'Place Order'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default OrderModal;
