import React, { useState } from 'react';
import OrderModal from './OrderModal';
import { useTranslation } from 'react-i18next';

function BuyerProductBrowse() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({ crop_type: '', pin_code: '' });
  const [orderProduct, setOrderProduct] = useState(null);
  const [orderSuccess, setOrderSuccess] = useState('');
  const [hasSearched, setHasSearched] = useState(false);
  const { t } = useTranslation();

  const fetchProducts = async () => {
    if (!filters.crop_type.trim()) {
      setError('Please enter a crop type to search');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const params = new URLSearchParams();
      if (filters.crop_type) params.append('crop_type', filters.crop_type);
      if (filters.pin_code) params.append('pin_code', filters.pin_code);
      const res = await fetch(`http://localhost:5000/api/products?${params.toString()}`);
      const data = await res.json();
      if (!data.success) {
        setError(data.message || 'Failed to fetch products');
      } else {
        setProducts(data.products);
        setHasSearched(true);
      }
    } catch (err) {
      setError('Network error');
    }
    setLoading(false);
  };

  const handleFilterChange = e => {
    setFilters(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleFilterSubmit = e => {
    e.preventDefault();
    fetchProducts();
  };

  const handleOrderClick = product => {
    setOrderProduct(product);
    setOrderSuccess('');
  };

  const handleOrderPlaced = () => {
    setOrderSuccess('Order placed successfully!');
    setOrderProduct(null);
  };

  return (
    <div>
      <h3 className="mb-4 text-center" style={{ color: '#388e3c', fontWeight: 600 }}>{t('search_fresh_crops')}</h3>

      {!hasSearched && (
        <div className="text-center mb-4">
          <p className="text-muted">{t('enter_crop_type_find_products')}</p>
        </div>
      )}

      <form className="row g-3 mb-4" onSubmit={handleFilterSubmit}>
        <div className="col-md-5">
          <input 
            type="text" 
            className="form-control eco-input" 
            name="crop_type" 
            placeholder={t('enter_crop_type_placeholder')} 
            value={filters.crop_type} 
            onChange={handleFilterChange}
            required
          />
        </div>
        <div className="col-md-4">
          <input 
            type="text" 
            className="form-control eco-input" 
            name="pin_code" 
            placeholder={t('farmer_pin_code_optional')} 
            value={filters.pin_code} 
            onChange={handleFilterChange} 
          />
        </div>
        <div className="col-md-3">
          <button type="submit" className="btn eco-btn w-100" disabled={loading}>
            {loading ? t('searching') : t('search')}
          </button>
        </div>
      </form>

      {orderSuccess && <div className="alert alert-success">{t(orderSuccess) || orderSuccess}</div>}
      {error && <div className="alert alert-danger">{t(error) || error}</div>}

      {loading && (
        <div className="text-center my-4">
          <div className="spinner-border text-success" />
          <p className="mt-2">{t('searching_fresh_crops')}</p>
        </div>
      )}

      {hasSearched && !loading && (
        <div>
          {products.length > 0 ? (
            <div className="row">
              {products.map(product => (
                <div className="col-md-4 mb-4" key={product.id}>
                  <div className="card h-100 shadow-sm">
                    {product.image && (
                      <img src={`http://localhost:5000/api/products/images/${product.image}`} alt={product.name} className="card-img-top" style={{ maxHeight: 180, objectFit: 'cover' }} />
                    )}
                    <div className="card-body">
                      <h5 className="card-title">{product.name}</h5>
                      <div className="mb-2 text-muted">{product.crop_type}</div>
                      <div>Price: ₹{product.price} / {product.unit}</div>
                      <div>Quantity: {product.quantity}</div>
                      {product.location && <div>Location: {product.location}</div>}
                      <div>Farmer PIN: {product.farmer_pin}</div>
                      {product.description && <div className="mt-2"><small>{product.description}</small></div>}
                      <button className="btn btn-success mt-3 w-100" onClick={() => handleOrderClick(product)}>Order</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center my-4">
              <p className="text-muted">No products found for "{filters.crop_type}". Try a different crop type or PIN code.</p>
            </div>
          )}
        </div>
      )}

      <OrderModal show={!!orderProduct} onClose={() => setOrderProduct(null)} product={orderProduct} onOrderPlaced={handleOrderPlaced} />
    </div>
  );
}

export default BuyerProductBrowse;
